function mainSlider1Init() {
    var swiperOptions = {
        loop: true,
        speed: 1000,
        parallax: true,
        effect: 'fade',
        autoplay: {
            delay: 3500,
            disableOnInteraction: false,
        },
        grabCursor: false,

        navigation: {
            nextEl: '.swiper-button-next',
            prevEl: '.swiper-button-prev'
        },
    }

    var homeSlider = new Swiper('.homeSlider', swiperOptions)

    var pageSection = $('.bg-image')
    pageSection.each(function(indx) {
        if ($(this).attr('data-background')) {
            $(this).css('background-image', 'url(' + $(this).data('background') + ')')
        }
    })

}
mainSlider1Init();


//gsap loader
const tl = gsap.timeline({ duration: 0.1 });

function preLoader(tl) {
    tl.to('.preLoader.black > img', {
            scale: 1.1,
            autoAlpha: 0
        })
        .to('.preLoader.black', {
            yPercent: -100
        })
        .to('.preLoader.white', {
            yPercent: -100
        })
        .to('.preLoader', {
            css: {
                display: 'none'
            }
        })
        .from('.navbar-brand > img', {
            x: -50,
            autoAlpha: 0
        }, '-=0.5')
        .from('.navbar-nav > li', {
            autoAlpha: 0,
            x: 100,
            stagger: 0.05
        }, '-=0.5')
        .from('.slideOne', {
            autoAlpha: 0,
            yPercent: 100,
            stagger: 0.05
        }, '-=0.5');
}

// function initialization
$(window).on('load', function() {
    let str = window.location.href;
    str = str.split('/');
    if (str.at(-1) === 'index.php' || str.at(-1) === '') {
        preLoader(tl);
    } else {}
});

